override func viewDidLayoutSubviews() {
	super.viewDidLayoutSubviews()
// Flash scroll indicator if content long enough for scroll
	itemDescription.layoutIfNeeded()
	// This line is needed because the autolayout constraints may not be in effect yet
	let contentHeight = itemDescription.contentSize.height
	let viewHeight = itemDescription.frame.height
	if contentHeight > viewHeight {
		itemDescription.flashScrollIndicators()
	}
}

@IBOutlet weak var refresher: UIRefreshControl!
@IBAction func refreshControlActivated(_ sender:UIRefreshControl) {
	let refreshingString = NSAttributedString(string: "Refreshing", attributes: [NSAttributedStringKey.font : UIFont.systemFont(ofSize: 18)])
	let refreshDefaultString = NSAttributedString(string: "Pull Down To Refresh", attributes: [NSAttributedStringKey.font : UIFont.systemFont(ofSize: 12)])
	refresher.attributedTitle = refreshingString
	tableView.reloadData()
	DispatchQueue.main.asyncAfter(deadline: .now() + 1.3) {
	// dummy here, only creates delay
		sender.endRefreshing()
		self.refresher.attributedTitle = refreshDefaultString
	}
}

