let gregorianCalendar: Calendar = Calendar(identifier: .gregorian)
let currentDate = gregorianCalendar.startOfDay(for: Date())
let dobDatePicker = UIDatePicker()
dobDatePicker.minimumDate = gregorianCalendar.date(byAdding: .year, value: -100, to: currentDate)
dobDatePicker.maximumDate = gregorianCalendar.date(byAdding: .year, value: -16, to: currentDate)

let animalArray:[String] = ["dog","cat","dog","dog","Parrot","Parrot"]
var countAnimal:[String: Int] = [:]
for animal in animalArray {
	countAnimal[animal] = (countAnimal[animal] ?? 0) + 1
	//RHS = countAnimal[dog] if not nil, current value; if nil return 0 instead of nil
}
print(countAnimal)										// ["Parrot": 2, "cat": 1, "dog": 3]
print(countAnimal["rabbit"] ?? "no rabbit")				// no rabbit
let sortedAnimal = countAnimal.sorted(by: { $0.1 > $1.1 })
print(sortedAnimal)										// [(key: "dog", value: 3), (key: "Parrot", value: 2), (key: "cat", value: 1)]
let topAnimal = countAnimal.sorted(by: { $0.1 > $1.1 }).first!.key
print(topAnimal)										// dog

override func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
    let cell = tableView.dequeueReusableCell(withIdentifier: "MenuItemCell", for: indexPath)
	let menuItem = menuItems[indexPath.row]

	MenuController.shared.fetchItemImage(imageURL: menuItem.imageURL) { (image) in
		guard let image = image else {return}
		DispatchQueue.main.async {
			if let currentIndexPath = self.tableView.indexPath(for: cell), currentIndexPath != indexPath {return}
			// This is needed so that the right image will show up in the right cell after reuse
			// currentIndexPath: reusable cell index path, indexPath: currently rendering cell
			cell.imageView?.image = image
		}
	}
    return cell
}
